mapper.insert:
insert into sincard(
    title
    , price
    , inday

    , contents


    , category_idx


    , subcategory_idx

    , card_stat
) values(
    #{title}
    , #{price}
    , #{inday}

    , #{contents}


    , (select idx from category where title = #{category})


    , (select idx from category where title = #{subcategory})

    , '0'
)

mapper.select:
select a.month
from(
    select extract(month from to_date(inday, 'yyyy-mm-dd')) as month
    from sincard
) as a
group by a.month
